jQuery(function($){

  function media_upload(button_selector) {
  // Set all variables to be used in scope
  var frame;

      // ADD IMAGE LINK
      $('body').on( 'click', button_selector, function( event ){

        event.preventDefault();

        var button_id = $(this).data('id');



        // Create a new media frame
        frame = wp.media({
          title: 'Wybierz lub prześlij media',
          button: {
            text: 'Użyj to zdjęcie'
          },
          multiple: false  // Set to true to allow multiple files to be selected
        });


        // When an image is selected in the media frame...
        frame.on( 'select', function() {


          // Get media attachment details from the frame state
          var attachment = frame.state().get('selection').first().toJSON();


          $('.' + button_id + '_url').val(attachment.url).change();
          $('.' + button_id + '_img').attr('src', attachment.url).show();

          $('#' + button_id + '_up').parent().addClass( 'hidden-wpg' );


          frame.close();
        });

        // Finally, open the modal on click
        frame.open();
      });

      $('body').on( 'click', '.js_custom_remove_media', function( event ){

        var button_id = $(this).data('id');

        $('#' + button_id + '_del').parent().removeClass( 'hidden-wpg' );

        $('.' + button_id + '_url').val('').change();
        $('.' + button_id + '_img').hide();
      })
}
media_upload('.js_custom_upload_media');


});
